import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { Job } from '../types';
import { jobApi } from '../services/api';
import VisualJobBuilder from '../components/VisualJobBuilder';
import { Plus, Play, Edit, Trash2, History, X, RefreshCw, Save, Download, Upload } from 'lucide-react';

const Jobs: React.FC = () => {
  const navigate = useNavigate();
  const { action, id } = useParams<{ action?: string; id?: string }>();
  const [jobs, setJobs] = useState<Job[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedJob, setSelectedJob] = useState<Job | null>(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const isEditing = action === 'edit' && id;
  const isCreating = action === 'create';

  useEffect(() => {
    fetchJobs();
  }, []);

  useEffect(() => {
    if (isEditing && id) {
      const job = jobs.find(j => j.id === parseInt(id));
      if (job) {
        setSelectedJob(job);
        setShowCreateForm(true);
      }
    } else if (isCreating) {
      setSelectedJob(null);
      setShowCreateForm(true);
    }
  }, [isEditing, isCreating, id, jobs]);

  const fetchJobs = async () => {
    try {
      setLoading(true);
      const response = await jobApi.list();
      setJobs(response.jobs || []);
    } catch (error: any) {
      console.error('Failed to fetch jobs:', error);
      setError(error.message || 'Failed to load jobs');
      setJobs([]);
    } finally {
      setLoading(false);
    }
  };



  const handleDelete = async (jobId: number) => {
    if (window.confirm('Delete this job? This action cannot be undone.')) {
      try {
        await jobApi.delete(jobId);
        fetchJobs();
        if (selectedJob?.id === jobId) {
          setSelectedJob(null);
        }
      } catch (error: any) {
        console.error('Failed to delete job:', error);
        setError(error.message || 'Failed to delete job');
      }
    }
  };

  const handleRunJob = async (jobId: number) => {
    try {
      await jobApi.run(jobId);
      alert('Job started successfully!');
    } catch (error: any) {
      console.error('Failed to run job:', error);
      alert('Failed to start job. Check the logs for details.');
    }
  };

  const handleJobSaved = async (jobData: any) => {
    try {
      console.log('Saving job with data:', JSON.stringify(jobData, null, 2));

      if (isEditing && selectedJob) {
        // Update existing job
        await jobApi.update(selectedJob.id, jobData);
        console.log('Job updated successfully');
      } else {
        // Create new job
        await jobApi.create(jobData);
        console.log('Job created successfully');
      }

      // Refresh jobs list and navigate back
      await fetchJobs();
      setShowCreateForm(false);
      navigate('/job-management');
    } catch (error: any) {
      console.error('Failed to save job:', error);
      console.error('Error response:', error.response?.data);
      console.error('Error response (stringified):', JSON.stringify(error.response?.data, null, 2));
      console.error('Error status:', error.response?.status);
      console.error('Error headers:', error.response?.headers);

      // Try to extract the actual error message
      let errorMessage = 'Unknown error';
      if (error.response?.data) {
        const errorData = error.response.data;
        if (typeof errorData === 'string') {
          errorMessage = errorData;
        } else if (errorData.error) {
          if (typeof errorData.error === 'string') {
            errorMessage = errorData.error;
          } else if (errorData.error.message) {
            errorMessage = errorData.error.message;
          } else {
            errorMessage = JSON.stringify(errorData.error);
          }
        } else if (errorData.message) {
          errorMessage = errorData.message;
        } else if (errorData.detail) {
          errorMessage = errorData.detail;
        } else {
          errorMessage = JSON.stringify(errorData);
        }
      } else if (error.message) {
        errorMessage = error.message;
      }

      setError(errorMessage);
      alert('Failed to save job: ' + errorMessage);
    }
  };

  const handleJobSelect = (job: Job) => {
    setSelectedJob(job);
  };

  const handleExportJobs = async () => {
    try {
      const response = await jobApi.export();
      const blob = new Blob([JSON.stringify(response, null, 2)], { type: 'application/json' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `jobs-export-${new Date().toISOString().split('T')[0]}.json`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (error: any) {
      console.error('Failed to export jobs:', error);
      setError(error.message || 'Failed to export jobs');
    }
  };

  const handleImportJobs = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = '.json';
    input.onchange = async (e) => {
      const file = (e.target as HTMLInputElement).files?.[0];
      if (!file) return;

      try {
        const text = await file.text();
        const importData = JSON.parse(text);
        const result = await jobApi.import(importData);

        // Show success message
        alert(`Import completed: ${result.imported_count} jobs imported, ${result.failed_count} failed`);

        // Refresh the jobs list
        fetchJobs();
      } catch (error: any) {
        console.error('Failed to import jobs:', error);
        setError(error.message || 'Failed to import jobs');
      }
    };
    input.click();
  };

  const getJobStatusBadge = (status: string) => {
    switch (status?.toLowerCase()) {
      case 'active':
      case 'enabled':
        return 'status-badge-success';
      case 'inactive':
      case 'disabled':
        return 'status-badge-neutral';
      case 'error':
        return 'status-badge-danger';
      default:
        return 'status-badge-info';
    }
  };



  if (loading && jobs.length === 0) {
    return (
      <div className="loading-overlay">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  // If showing the form, render it full-screen
  if (showCreateForm) {
    return (
      <div className="dense-dashboard">
        <style>
          {`
            .dense-dashboard {
              padding: 8px 12px;
              max-width: 100%;
              font-size: 13px;
            }
            .dashboard-header {
              display: flex;
              justify-content: space-between;
              align-items: center;
              margin-bottom: 12px;
              padding-bottom: 8px;
              border-bottom: 1px solid var(--neutral-200);
            }
            .dashboard-header h1 {
              font-size: 20px;
              font-weight: 600;
              margin: 0;
              color: var(--neutral-800);
            }
            .header-subtitle {
              font-size: 12px;
              color: var(--neutral-600);
              margin: 2px 0 0 0;
            }
          `}
        </style>

        {/* Dashboard-style header */}
        <div className="dashboard-header">
          <div className="header-left">
            <h1>{isEditing ? 'Edit Job' : 'Create Job'}</h1>
            <p className="header-subtitle">Design and configure your job workflow using drag and drop</p>
          </div>
          <div className="header-actions">
            <button
              className="btn-icon btn-ghost"
              onClick={() => {
                // Trigger refresh steps in VisualJobBuilder
                window.dispatchEvent(new CustomEvent('refreshSteps'));
              }}
              title="Refresh Steps"
            >
              <RefreshCw size={16} />
            </button>
            <button
              className="btn-icon btn-success"
              onClick={() => {
                // Trigger save job in VisualJobBuilder
                window.dispatchEvent(new CustomEvent('saveJob'));
              }}
              title="Save Job"
            >
              <Save size={16} />
            </button>
            <button 
              className="btn-icon btn-ghost"
              onClick={() => {
                setShowCreateForm(false);
                navigate('/job-management');
              }}
              title="Cancel"
            >
              <X size={16} />
            </button>
          </div>
        </div>

        <VisualJobBuilder
          editingJob={selectedJob}
          onJobCreate={handleJobSaved}
          onCancel={() => {
            setShowCreateForm(false);
            navigate('/job-management');
          }}
          onRefreshSteps={() => {
            // Trigger refresh steps in VisualJobBuilder
            window.dispatchEvent(new CustomEvent('refreshSteps'));
          }}
          onSaveJob={() => {
            // Trigger save job in VisualJobBuilder
            window.dispatchEvent(new CustomEvent('saveJob'));
          }}
        />
      </div>
    );
  }

  return (
    <div className="dense-dashboard">
      <style>
        {`
          /* Dashboard-style layout - EXACT MATCH */
          .dense-dashboard {
            padding: 8px 12px;
            max-width: 100%;
            font-size: 13px;
          }
          .dashboard-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 12px;
            padding-bottom: 8px;
            border-bottom: 1px solid var(--neutral-200);
          }
          .header-left h1 {
            font-size: 18px;
            font-weight: 600;
            margin: 0;
            color: var(--neutral-800);
          }
          .header-subtitle {
            font-size: 12px;
            color: var(--neutral-600);
            margin: 2px 0 0 0;
          }
          .dashboard-grid {
            display: grid;
            grid-template-columns: 2fr 1fr;
            gap: 12px;
            align-items: stretch;
            height: calc(100vh - 110px);
          }
          .dashboard-section {
            background: white;
            border: 1px solid var(--neutral-200);
            border-radius: 6px;
            overflow: hidden;
            display: flex;
            flex-direction: column;
            height: 100%;
          }
          .section-header {
            background: var(--neutral-50);
            padding: 8px 12px;
            font-weight: 600;
            font-size: 13px;
            color: var(--neutral-700);
            border-bottom: 1px solid var(--neutral-200);
            display: flex;
            justify-content: space-between;
            align-items: center;
          }
          .compact-content {
            padding: 0;
            flex: 1;
            display: flex;
            flex-direction: column;
            overflow: auto;
          }
          .table-container {
            flex: 1;
            overflow: auto;
          }

          /* Jobs table styles */
          .jobs-table {
            width: 100%;
            border-collapse: collapse;
            font-size: 12px;
          }
          .jobs-table th {
            background: var(--neutral-50);
            padding: 6px 8px;
            text-align: left;
            font-weight: 600;
            color: var(--neutral-700);
            border-bottom: 1px solid var(--neutral-200);
            font-size: 11px;
          }
          .jobs-table td {
            padding: 6px 8px;
            border-bottom: 1px solid var(--neutral-100);
            vertical-align: middle;
            font-size: 12px;
          }
          .jobs-table tr:hover {
            background: var(--neutral-50);
          }
          .jobs-table tr.selected {
            background: var(--primary-blue-light);
            border-left: 3px solid var(--primary-blue);
          }
          .jobs-table tr {
            cursor: pointer;
          }



          /* Details panel */
          .details-panel {
            padding: 8px;
          }
          .details-panel h3 {
            margin: 0 0 12px 0;
            font-size: 14px;
            font-weight: 600;
            color: var(--neutral-800);
          }
          .detail-group {
            margin-bottom: 12px;
          }
          .detail-label {
            font-size: 10px;
            font-weight: 600;
            color: var(--neutral-500);
            margin-bottom: 3px;
            text-transform: uppercase;
            letter-spacing: 0.5px;
          }
          .detail-value {
            font-size: 12px;
            color: var(--neutral-800);
            padding: 6px 0;
            border-bottom: 1px solid var(--neutral-100);
          }

          /* Button styles */
          .btn-icon {
            display: inline-flex;
            align-items: center;
            justify-content: center;
            width: 24px;
            height: 24px;
            border: none;
            background: none;
            cursor: pointer;
            transition: all 0.15s;
            margin: 0 1px;
            padding: 2px;
          }
          .btn-icon:hover {
            opacity: 0.7;
          }
          .btn-icon:disabled {
            opacity: 0.3;
            cursor: not-allowed;
          }
          .btn-success {
            color: var(--success-green);
          }
          .btn-success:hover:not(:disabled) {
            color: var(--success-green-dark);
          }
          .btn-info {
            color: var(--primary-blue);
          }
          .btn-info:hover:not(:disabled) {
            color: var(--primary-blue-dark);
          }
          .btn-danger {
            color: var(--danger-red);
          }
          .btn-danger:hover:not(:disabled) {
            color: var(--danger-red);
          }
          .btn-ghost {
            color: var(--neutral-500);
          }
          .btn-ghost:hover:not(:disabled) {
            color: var(--neutral-700);
          }
          .btn-primary {
            background: var(--primary-blue);
            color: white;
            padding: 6px 12px;
            border: none;
            border-radius: 4px;
            font-size: 12px;
            cursor: pointer;
          }
          .btn-primary:hover:not(:disabled) {
            background: var(--primary-blue-dark);
          }
          .btn-primary:disabled {
            opacity: 0.5;
            cursor: not-allowed;
          }

          .action-buttons {
            display: flex;
            gap: 4px;
            margin-top: 12px;
            padding-top: 12px;
            border-top: 1px solid var(--neutral-200);
          }

          .empty-state {
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            height: 150px;
            color: var(--neutral-500);
            text-align: center;
          }
          .empty-state h3 {
            margin: 0 0 6px 0;
            font-size: 14px;
            font-weight: 600;
          }
          .empty-state p {
            margin: 0 0 12px 0;
            font-size: 12px;
          }

          .status-badge {
            display: inline-block;
            padding: 2px 6px;
            border-radius: 12px;
            font-size: 10px;
            font-weight: 500;
            text-transform: uppercase;
            letter-spacing: 0.5px;
          }
          .status-badge-success {
            background: var(--success-green-light);
            color: var(--success-green);
          }
          .status-badge-danger {
            background: var(--danger-red-light);
            color: var(--danger-red);
          }
          .status-badge-warning {
            background: var(--warning-yellow-light);
            color: var(--warning-yellow);
          }
          .status-badge-info {
            background: var(--primary-blue-light);
            color: var(--primary-blue);
          }
          .status-badge-neutral {
            background: var(--neutral-200);
            color: var(--neutral-600);
          }

          .alert {
            background: var(--danger-red-light);
            color: var(--danger-red);
            padding: 8px 12px;
            border-radius: 4px;
            margin-bottom: 12px;
            font-size: 12px;
          }

          .job-steps-count {
            font-size: 10px;
            color: var(--neutral-500);
          }
        `}
      </style>

      {/* Dashboard-style header */}
      <div className="dashboard-header">
        <div className="header-left">
          <h1>Job Management</h1>
          <p className="header-subtitle">Create, manage, and monitor automated job executions</p>
        </div>
        <div className="header-actions">
          <button 
            className="btn-icon btn-info"
            onClick={handleImportJobs}
            title="Import jobs"
          >
            <Download size={16} />
          </button>
          <button 
            className="btn-icon btn-info"
            onClick={handleExportJobs}
            title="Export jobs"
          >
            <Upload size={16} />
          </button>
          <button 
            className="btn-icon btn-success"
            onClick={() => {
              setShowCreateForm(true);
              navigate('/job-management/create');
            }}
            title="Create new job"
          >
            <Plus size={16} />
          </button>
        </div>
      </div>

      {error && (
        <div className="alert">
          {error}
          <button 
            style={{ float: 'right', background: 'none', border: 'none', color: 'inherit', cursor: 'pointer' }}
            onClick={() => setError(null)}
          >

          </button>
        </div>
      )}

      {/* 2-column dashboard grid */}
      <div className="dashboard-grid">
        {/* Column 1: Jobs */}
        <div className="dashboard-section">
          <div className="section-header">
            Jobs ({jobs.length})
            <div style={{ display: 'flex', gap: '2px' }}>
              <button 
                className="btn-icon btn-info"
                onClick={handleImportJobs}
                title="Import jobs"
              >
                <Download size={14} />
              </button>
              <button 
                className="btn-icon btn-info"
                onClick={handleExportJobs}
                title="Export jobs"
              >
                <Upload size={14} />
              </button>
              <button 
                className="btn-icon btn-success"
                onClick={() => {
                  setShowCreateForm(true);
                  navigate('/job-management/create');
                }}
                title="Create new job"
              >
                <Plus size={14} />
              </button>
            </div>
          </div>
          <div className="compact-content">
            {jobs.length === 0 ? (
              <div className="empty-state">
                <h3>No jobs found</h3>
                <p>Create your first job to automate tasks on your targets.</p>
                <button 
                  className="btn-icon btn-success"
                  onClick={() => {
                    setShowCreateForm(true);
                    navigate('/job-management/create');
                  }}
                  title="Create first job"
                >
                  <Plus size={16} />
                </button>
              </div>
            ) : (
              <div className="table-container">
                <table className="jobs-table">
                  <thead>
                    <tr>
                      <th>Name</th>
                      <th>Status</th>
                      <th>Created</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {jobs.map((job) => (
                      <tr 
                        key={job.id} 
                        className={selectedJob?.id === job.id ? 'selected' : ''}
                        onClick={() => handleJobSelect(job)}
                      >
                        <td>
                          <div style={{ fontWeight: '500' }}>{job.name}</div>
                          <div className="job-steps-count">ID: {job.id}</div>
                        </td>
                        <td>
                          <span className={`status-badge ${getJobStatusBadge('active')}`}>
                            Active
                          </span>
                        </td>
                        <td style={{ fontSize: '11px', color: 'var(--neutral-500)' }}>
                          {new Date(job.created_at).toLocaleDateString()}
                        </td>
                        <td>
                          <div style={{ display: 'flex', gap: '2px' }}>
                            <button 
                              className="btn-icon btn-success"
                              onClick={(e) => {
                                e.stopPropagation();
                                handleRunJob(job.id);
                              }}
                              title="Run job"
                            >
                              <Play size={14} />
                            </button>
                            <button 
                              className="btn-icon btn-info"
                              onClick={(e) => {
                                e.stopPropagation();
                                navigate(`/history/job-runs?job_id=${job.id}`);
                              }}
                              title="View job history"
                            >
                              <History size={14} />
                            </button>
                            <button 
                              className="btn-icon btn-ghost"
                              onClick={(e) => {
                                e.stopPropagation();
                                setSelectedJob(job);
                                setShowCreateForm(true);
                                navigate(`/job-management/edit/${job.id}`);
                              }}
                              title="Edit job"
                            >
                              <Edit size={14} />
                            </button>
                            <button 
                              className="btn-icon btn-danger"
                              onClick={(e) => {
                                e.stopPropagation();
                                handleDelete(job.id);
                              }}
                              title="Delete job"
                            >
                              <Trash2 size={14} />
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </div>

        {/* Column 2: Details Panel */}
        <div className="dashboard-section">
          <div className="section-header">
            {selectedJob ? 'Job Details' : 'Select Job'}
          </div>
          <div className="compact-content">
            {selectedJob ? (
              <div className="details-panel">
                <h3>{selectedJob.name}</h3>

                <div className="detail-group">
                  <div className="detail-label">ID</div>
                  <div className="detail-value">{selectedJob.id}</div>
                </div>

                <div className="detail-group">
                  <div className="detail-label">Status</div>
                  <div className="detail-value">
                    <span className={`status-badge ${getJobStatusBadge('active')}`}>
                      Active
                    </span>
                  </div>
                </div>

                <div className="detail-group">
                  <div className="detail-label">Created</div>
                  <div className="detail-value">{new Date(selectedJob.created_at).toLocaleString()}</div>
                </div>

                <div className="detail-group">
                  <div className="detail-label">Updated</div>
                  <div className="detail-value">{new Date(selectedJob.updated_at).toLocaleString()}</div>
                </div>



                <div className="action-buttons" style={{ display: 'flex', gap: '8px', flexWrap: 'wrap', marginTop: '16px' }}>
                  <button 
                    className="btn-icon btn-success"
                    onClick={() => handleRunJob(selectedJob.id)}
                    title="Run job"
                  >
                    <Play size={16} />
                  </button>
                  <button 
                    className="btn-icon btn-info"
                    onClick={() => navigate(`/history/job-runs?job_id=${selectedJob.id}`)}
                    title="View job history"
                  >
                    <History size={16} />
                  </button>
                  <button 
                    className="btn-icon btn-ghost"
                    onClick={() => {
                      setShowCreateForm(true);
                      navigate(`/job-management/edit/${selectedJob.id}`);
                    }}
                    title="Edit job"
                  >
                    <Edit size={16} />
                  </button>
                  <button 
                    className="btn-icon btn-danger"
                    onClick={() => handleDelete(selectedJob.id)}
                    title="Delete job"
                  >
                    <Trash2 size={16} />
                  </button>
                </div>
              </div>
            ) : (
              <div className="empty-state">
                <p>Select a job to view details</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Jobs;
